mapper.select:
select t.id , t.master_id , t.send_id , t.name , t.content , t.create_time , t.is_active from
t_contract_master_msg t


        and t.id = #{contracmasterMsgVo.id}


        and t.master_id = #{contracmasterMsgVo.masterId}


        and t.send_id = #{contracmasterMsgVo.sendId}


        and t.name = #{contracmasterMsgVo.name}


        and t.content = #{contracmasterMsgVo.content}


        and t.create_time = #{contracmasterMsgVo.createTime}


        and t.is_active = #{contracmasterMsgVo.isActive}





        order by t.${contracmasterMsgVo.sort} t.${contracmasterMsgVo.order}


        order by t.id desc



    limit #{pageQuery.pageNo}, #{pageQuery.pageSize}

select count(t.id) from t_contract_master_msg t


        and t.id = #{contracmasterMsgVo.id}


        and t.master_id = #{contracmasterMsgVo.masterId}


        and t.send_id = #{contracmasterMsgVo.sendId}


        and t.name = #{contracmasterMsgVo.name}


        and t.content = #{contracmasterMsgVo.content}


        and t.create_time = #{contracmasterMsgVo.createTime}


        and t.is_active = #{contracmasterMsgVo.isActive}





        order by t.${contracmasterMsgVo.sort} ${contracmasterMsgVo.order}


        order by t.id desc

mapper.update:
update t_contract_master_msg
master_id = #{masterId}, send_id = #{sendId}, name = #{name}, content = #{content}, create_time = #{createTime}, is_active = #{isActive}
where id = #{id}
