mapper.update:
update t_contract_master

    bank_name = #{bankName
            },

    bank_account_name = #{bankAccountName
            },

    bank_account_no = #{bankAccountNo
            },

    company_id = #{companyId
            },

    phone = #{phone
            },

    password = #{password
            },

    identity = #{identity
            },

    wxopenid = #{wxopenid
            },

    face_img = #{faceImg
            },

    nickname = #{nickname
            },

    scale = #{scale
            },

    is_active = #{isActive
            },

    create_time = #{createTime
            }


where id = #{id}

mapper.select:
select id,bank_name,bank_account_name,bank_account_no,is_active,company_id,phone,`identity`,wxopenid,create_time,face_img,nickname from t_contract_master

    and wxopenid=#{wxopenid}
    and phone = #{phone}
    and password=#{password}

select id from t_contract_master

    and wxopenid=#{wxopenid}
    and phone = #{phone}
